exports.step = function(input) {
  const terminationOptions = input.options.filter(i => i.type === 'termination').reduce((acc, t) => {
    acc[t.id] = t.name;
    return acc;
  }, {});

	return {
    name: input.record.custrecord_cr_name.substring(0,80),
    hasAutoRenewalClause: input.record.custrecord_cr_has_autorenewal_clause === 'T',
    customerContractExtensionRight: input.record.custrecord_cr_customer_contract_extensio === 'T',
    restrictionOnNewBuyer: input.record.custrecord_cr_restriction_on_new_buyer === 'T',
    limitationOfLiability: input.record.custrecord_cr_limitation_of_liability === 'T',
    disclaimerOfIndirectDamages: input.record.custrecord_cr_disclaimer_of_indirect_dam === 'T',
    mfnClause: input.record.custrecord_cr_mfn_clause_affiliates === 'T',
    federalGovernementContract: input.record.custrecord_cr_federal_gov_contract === 'T',
    canCustomerTerminateAtConvenience: input.record.custrecord_cr_ct_at_convenience === 'T',
    canCustomerTerminateAtChangeOfControl: input.record.custrecord_cr_ct_at_change_of_control === 'T',
    providerHasRightNotToRenew: input.record.custrecord_cr_pt_has_right_not_to_renew === 'T',
    transferRightsByProvider: findOptionName('transfer_of_rights', input.record.custrecord_cr_transfer_rights_by_provide),
    useOfForeignEntities: findOptionName('foreign_entities', input.record.custrecord_cr_use_of_foreign_entities),
    capsOnRenewalPrice: findOptionName('caps', input.record.custrecord_cr_caps_on_renewal_price),
    customerRightToReduceEntitlements: findOptionName('entitlements', input.record.custrecord_cr_customer_reduce_entitlemen),
    contractType: findOptionName('contract_type', input.record.custrecord_cr_contract_type),
    masterAgreementPaper: findOptionName('paper_type', input.record.custrecord_cr_master_agreement_paper),
    latestSignedQuotePaper: findOptionName('paper_type', input.record.custrecord_cr_latest_signed_quote_paper),
    reviewDate: formatDate(input.record.custrecord_cr_review_date),
    contractEndDate: formatDate(input.record.custrecord_cr_contract_end_date),
    masterAgreementEffectiveDate: formatDate(input.record.custrecord_cr_master_agr_effective_date),
    customerTerminationRefundPrepaidFees: formatPercent(input.record.custrecord_cr_ct_refund_prepaid_fees),
    contractTerm: formatNumber(input.record.custrecord_cr_contract_term),
    arCustomTerm: formatNumber(input.record.custrecord_cr_ar_custom_term),
    arNoticePeriod: formatNumber(input.record.custrecord_cr_provider_ar_notice_period),
    customerNonRenewalNoticePeriod: formatNumber(input.record.custrecord_cr_ct_notice_period),
    providerLockinPeriod: formatNumber(input.record.custrecord_cr_provider_lockin_period),
    providerNoticePeriodNotToRenew: formatNumber(input.record.custrecord_cr_pt_notice_period),
    indemnifiableLossesLiabilityLimitAmount: formatNumber(input.record.custrecord_cr_loss_liability_limit_amoun),
    providerPriceChangeNoticePeriod: formatNumber(input.record.custrecord_cr_pp_change_notice_period)
  };

	function formatNumber(number) {
    const result = parseFloat(number);
    if (isNaN(result)) {
      return null;
    } else {
      return result;
    }
  }

  function formatPercent(text) {
    if (text) {
      return formatNumber(text.replace('%',''));
    }
    return null;
  }

  function formatDate(date) {
    if (!date) {
      return null;
    }
    return new Date(date).toISOString().slice(0,10);
  }

 	function findOptionName(type, id) {
    return input.options.find(i => i.type === type && i.id === id)?.name
  }
};
