// Product the BU-related email out of the ISR user email
exports.step = function(input) {
  const name = input.ownerEmail.replace('.invalid','').replace(/@.*/g, '');
  const domain = input.mailboxOverride?input.mailboxOverride:(input.businessUnit.toLowerCase()+'.com');
	const url = 'mailboxes?filter[email]='+`${name}@${domain}`;
  return url;
};

// You can reference the input variables using input.NAME
// Parsed JSON files could be referenced as fileInput
exports.step = function(input, fileInput) {
  if(input.status==='Signed') {
    return 156;
  }
  if(input.status==='Out for Signature') {
    if(input.isArQuote) {
      return 155;
    }
    return 154;
  }
};

