exports.step = function(input) {
	var filtered = input.items.filter(row=> row.id === input.itemId);
	// order by row.fromval
	var sorted = filtered.sort((a,b) => a.fromval - b.fromval);
	return sorted;
};

exports.step = function(input) {
  let tiers = input.tiers;
  console.log('tiers', tiers);
  let nextTier = tiers.find(tier => parseInt(tier.fromval) > parseInt(input.current.fromval));
  console.log('nextTier', nextTier);
  let lowerBound = input.current.fromval;
  let upperBound = nextTier ? nextTier.fromval : null;  
	return {
  	name : input.prodCode + " " + input.productTier + " " + input.current.fromval + '-'+ (upperBound?upperBound:'inf'),
    lowerBound : parseInt(lowerBound), //+1 increment is not needed
    upperBound : parseInt(upperBound), //+1 increment is not needed
    price: input.current.value
  };
};

// Sum the support and add-ons prices,
// Zero out the support prices
exports.step = function (input) {
  if(input.zeroOutPrices){
    // Zero out values for input.prices array
    input.prices.forEach(function(priceObject) {
        priceObject.value = "0";
    });
  }
    // Grouping items with same first 3 parts of subscriptionPlanName
    const groupedPrices = input.prices.reduce((accumulator, currentPrice) => {
        const { subscriptionPlanName } = input;
        const [tier1, tier2, tier3] = subscriptionPlanName.split('-');
        const regex = new RegExp(`^${tier1}-${tier2}-${tier3}`);
        const isMatching = currentPrice.fullname.match(regex);

        if (isMatching) {
            // Zero out the main and support products prices
            accumulator[`${currentPrice.fullname}${currentPrice.fromval}`] = { ...currentPrice };
            accumulator[`${currentPrice.fullname}${currentPrice.fromval}`].value = 0;
            // Add the 'Main' product with summed price
            const key = `${tier1}-${tier2}-${tier3}${currentPrice.fromval}`;
            if (!accumulator[key]) {
                accumulator[key] = accumulator[key] || { ...currentPrice };
                accumulator[key].fullname = 'Main';
                // Set plan id instead of an item id on the summed element
              	accumulator[key].id = input.planId;
                accumulator[key].value = 0;
            }

            console.log(`${key}-->${accumulator[key].value}+${currentPrice.value}`);
            accumulator[key].value =
                parseFloat(accumulator[key].value) + parseFloat(currentPrice.value);
        } else {

            accumulator[`${currentPrice.fullname}${currentPrice.fromval}`] = { ...currentPrice };
        }

        return accumulator;
    }, {});


    const output = {
        prices: Object.values(groupedPrices).map(price => {
            const { fullname } = price;

            if (fullname.endsWith('-ADD')) {
                const suffix = ['SIL', 'GOL', 'PLA'].find(suffix => {
                    return input.prices.some(
                        p =>
                            p.fullname === fullname.replace(/-ADD$/, `-${suffix}`) &&
                            p.fromval === price.fromval
                    );
                });

                if (suffix) {
                    const supportFullname = fullname.replace(
                        /-ADD$/,
                        `-${suffix}`
                    );
                    const addonPrice = parseFloat(price.value);
                    const supportPrice = parseFloat(
                        input.prices.find(
                            p =>
                                p.fullname === supportFullname && p.fromval === price.fromval
                        ).value
                    );
                    const correctedPrice = addonPrice + supportPrice;

                    return { ...price, value: `${correctedPrice}` };
                }
            } /*else if (
                (!input.subscriptionPlanName.includes('-MNT')) && (
                fullname.endsWith('-SIL') ||
                fullname.endsWith('-GOL') ||
                fullname.endsWith('-PLA'))
            ) {
                return { ...price, value: '0' };
            }*/

            return price;
        }),
    };

    return output.prices;
};

exports.step = function (input) {
    let tiers = input.tiers;
    console.log('tiers', tiers);
    let bound = input.tiers[1].fromval;
    return [{
        name: input.prodCode + '-fixed',
        lowerBound: 0,
        upperBound: bound,
        price: input.tiers[0].value,
        overage: null,
    },
    {
        name: input.prodCode + '-overage',
        lowerBound: bound,
        upperBound: null,
        price: input.tiers[0].value,
        overage: input.tiers[1].value,
    }];
};
