exports.step = function(input, fileInput) {
	// Minus sign is a special character
 const summary = input.summary.replace(/-/g,'\\\\-')
 		.replace(/\[/g,'\\\\[')
    .replace(/\]/g,'\\\\]');;
 const jql = `summary ~ "${summary}" AND status = Backlog order by created DESC`
 return jql;
};

