exports.step = function(input) {
	const regex = /^https:\/\/.+\/contact\/(?<id>\d+)$/;
	const contactId = input.url.match(regex).groups.id;
  return contactId;
};

exports.step = function(input) {
  const entityId = [ input.SFcontact.FirstName, input.SFcontact.MiddleName?.charAt(0), input.SFcontact.LastName ].filter(Boolean).join(' ');
	const contact = input.NScontacts.find(i => i.entityid === entityId);
  if (!contact) {
    return {
      contactFound: false,
      entityId: input.SFcontact.Name
    }
  }
  if (!contact.email) {
    return {
      contactFound: true,
      netsuiteId: contact.id
    }
  }

  if (input.SFcontact.Email && contact.email.toLowerCase() === input.SFcontact.Email.toLowerCase()) {
    return {
      contactFound: true,
      netsuiteId: contact.id
    }
  }
  for (let c = 1; c <= input.NScontacts.length; c++) {
    const entityId = `${input.SFcontact.Name} ${c}`;
    const matchEntity = input.NScontacts.find(i => i.entityid === entityId);  
    if (!matchEntity) {
      return {
        contactFound: false,
        entityId: entityId
      }
    }
  }
};

exports.step = function(input) {
	return {
    q: `SELECT id, entityid, email FROM contact
				WHERE company = ${input.company} 
					${addCondition('firstname', input.sfContact.FirstName)}
          ${addCondition('lastname', input.sfContact.LastName)}
          ${addCondition('email', input.sfContact.Email)}`
  }
};

function escapeQuote(str) {
    return str.replace(/'/g, "''");
}

function addCondition(field,value) {
  if(!value){
    return `AND (${field} IS NULL OR ${field}='') `;
  }
  return `AND ${field}='${escapeQuote(value)}' `;
}
