exports.step = function(input) {
  if (!input.website) {
    return null;
  }
  if(/^http:\/\/|^https:\/\//.test(input.website)){
  	return input.website;
  } else {
		return 'http://'+input.website;
  }
};

exports.step = function(input) {
	// Remove all non-digit characters from the Salesforce numbers
  const sfPhone = input.sfPhone ? input.sfPhone.replace(/\D/g, "") : undefined;
  const sfFax = input.sfFax ?  input.sfFax.replace(/\D/g, "") : undefined;

  // netsuite doesn't accept numbers with less than 7 digits
  return {
    phone: !sfPhone || sfPhone.length < 7 ? null : input.sfPhone,
    fax: !sfFax || sfFax.length < 7 ? null : input.sfFax
  }
};

exports.step = function (input) {
  let account = input.account;
  // Convert state to short name
  const mapStates = input.states.reduce((p,c,i)=>
  {
   p[c.fullname.toLowerCase()]=c.shortname; // FullName to short name mapping
   return p;
  },{});
  let state = account.ShippingState ? account.ShippingState.toLowerCase() : undefined;
  if(mapStates[state]){
    account.ShippingState = mapStates[state]; // We can use short names only
  }
  state = account.BillingState ? account.BillingState.toLowerCase() : undefined;
  if(mapStates[state]){
    account.BillingState = mapStates[state]; // We can use short names only
  }
  return {
    shippingState: account.ShippingState,
    billingState: account.BillingState
  }
};

exports.step = function (input) {
  let account = input.account;
  let addressBook = {
    items: [],
    addressChanged: false
  };

  account.ShippingCountry = input.countries.shippingCountry;
  account.ShippingState = input.states.shippingState;
  if (account.ShippingCountry && account.ShippingStreet) {
    const newAdress = {
      addressee: account.Name,
      country: { id: account.ShippingCountry },
      city: account.ShippingCity,
      addr1: account.ShippingStreet,
      state: account.ShippingState,
      zip: account.ShippingPostalCode
    };

    const itemToAdd = {
      addressBookAddress: newAdress,
      defaultShipping: true
    };
    addressBook.items.push(itemToAdd);
  }

  account.BillingCountry = input.countries.billingCountry;
  account.BillingState = input.states.billingState;
  if (account.BillingCountry && account.BillingStreet) {
    const newAdress = {
      addressee: account.Name,
      country: { id: account.BillingCountry },
      city: account.BillingCity,
      addr1: account.BillingStreet,
      state: account.BillingState,
      zip: account.BillingPostalCode
    };

    const itemToAdd = {
      addressBookAddress: newAdress,
      defaultBilling: true
    };
    addressBook.items.push(itemToAdd);
  }
  return addressBook;
};

