};

exports.step = function (input) {
  let account = input.account;
  //Convert country name to id if possible
  const mapCountries = input.countries.reduce((p,c,i)=>
  {
   p[c.name.toLowerCase()]=c.id; // Name to Id mapping
   return p;
  },{});
  mapCountries['usa'] = 'US';
  mapCountries['uk'] = 'GB';
  let country = account.ShippingCountry ? account.ShippingCountry.toLowerCase() : undefined;
  if(mapCountries[country]){
    account.ShippingCountry = mapCountries[country];
  }
  country = account.BillingCountry ? account.BillingCountry.toLowerCase() : undefined;
