// Since the parent subscription field is not available,
// we use suiteQL to get the parent subscription id
exports.step = function (input) {
  return {
    q:
      `SELECT p.parentsubscription, ps.custrecord_contract_docs FROM
(SELECT CASE WHEN hist.subscription IS NULL THEN custrecord_parent_subscription ELSE hist.subscription END parentsubscription
FROM subscription s
LEFT JOIN subscriptionRenewalHistory hist on hist.newsubscription = s.id
WHERE s.id = ${input.subscriptionId}) p
LEFT JOIN subscription ps ON p.parentsubscription = ps.id`
  };
};

exports.step = function (input) {
  const class_name = input.class.split(':').pop().trim();
  const product_name = class_name.replace(' Product','');
  return product_name;
};

exports.step = function(input, fileInput) {
  const initialRecord = input.initialRecord;
  const newRecord = input.newRecord;
  const updatedFields = [];

  for (const key in newRecord) {
    if (newRecord[key] !== initialRecord[key]) {
      updatedFields.push({ key, value: newRecord[key] });
    }
  }

  return { fields: updatedFields, length: updatedFields.length };
};

// Create Description with import messages
exports.step = function(input) {
  const today = new Date();
  const todayStr = `${(today.getMonth()+1).toString().padStart(2, '0')}/${today.getDate().toString().padStart(2, '0')}/${today.getFullYear().toString().substring(2,4)}`;
  let res =  `${todayStr} SYSTEM : Renewal opportunity imported from NetSuite. Subscription Name: "${input.name}"\n`
  if(input.legacy){
    res += `${todayStr} SYSTEM : Could not import products because this is a legacy NetSuite subscription record.\n`;
  }
  if(!input.currentTerm){ // Initial Term duration could not be determined
    res += `${todayStr} SYSTEM : Term could not be determined.`;
  }
	return res;
};

// Construct the product codes from the plan code
exports.step = function(input, fileInput) {
  const fields = input.plan.split("-");
  return {
    mainProductCode: `${fields[0]}-${fields[1]}-${fields[2]}-${fields[3]}`,
    supportProductCode: `${fields[0]}-${fields[1]}-${fields[2]}-${fields[4]}`,
    bundledProductCode: input.plan.replace( new RegExp("-\\d{3}$","gm"),""),
  };
};

