exports.step = function(input) {
  const getAccountName = (customer)=>
    customer.isPerson ? 
      [customer.firstName, customer.middleName, customer.lastName]
      .filter(f=>Boolean(f)) // filter out empty fields
      .join(' ')
  	: customer.companyName;
	const startDate=input.subscription.startDate;
  const prefix = 
        input.subscription.customer.id!=input.subscription.custrecord_subs_end_user.id?
        getAccountName(input.customer)
        +' - ':'';
  const name = `${prefix}${getAccountName(input.end_user??input.customer)} - ${input.product} - Renewal - ${parseInt(startDate.substring(5,7))+'_'+startDate.substring(0,4)}`;
  return name.substring(0,120); // To fit in the field
};

exports.step = function(input, fileInput) {
	const isOppUpdated = input.isOppUpdated;
  const areOppProductsSynced = input.areOppProductsSynced;

  if (isOppUpdated && areOppProductsSynced) {
    return "Parent Subscription data and opportunity products updated";
  } else if (areOppProductsSynced) {
    return "Opportunity products updated";
  } else {
  	return null; 
  }  
};

exports.step = function(input) {
  let threeMonthsAgo = new Date();
  threeMonthsAgo.setMonth(threeMonthsAgo.getMonth() - 3);
  threeMonthsAgo.setHours(0,0,0,0);
  const renewalDate = new Date(input.startDate + ' 00:00:00');
  return renewalDate < threeMonthsAgo;
}

// Legacy subscription plan code does not end with -XXX int version
exports.step = function(input, fileInput) {
  const m = input.planCode.match(/^.*-\d\d\d$/g);
  return !m;
};

// Since Currency is available in Beta only,
// we use suiteQL to get the code
exports.step = function (input) {
  return {
    q:
      `SELECT cur.symbol AS currencyisocode,
FROM subscription s
JOIN currency cur on s.currency = cur.id
WHERE s.id = ${input.subscriptionId}`
  };
};
