// Create Description with import messages
exports.step = function(input) {
  const today = new Date();
  const todayStr = `${(today.getMonth()+1).toString().padStart(2, '0')}/${today.getDate().toString().padStart(2, '0')}/${today.getFullYear().toString().substring(2,4)}`;
  let res = todayStr + ` SYSTEM: ${input.message}\n\n`
  res += input.old_description??""
	return res;
};

exports.step = function (input) {
  return {
    q:
      `SELECT  COALESCE(state.fullname, a.state) AS state,
        COALESCE(country.name,a.country)  AS country,
        a.addr1,
        a.city,
        a.zip
        FROM ContactAddressbookEntityAddress a 
        JOIN ContactAddressbook ca ON ca.addressbookaddress=a.nkey 
        LEFT JOIN state ON state.country=a.country AND state.shortname=a.state
        LEFT JOIN country ON a.country = country.id
        WHERE ca.defaultbilling='T' AND ca.entity=${input.id}`,
  };
};
