exports.step = function (input) {
  switch (input.tier) {
    case "End User":
      return "Customer";
    case "Reseller":
    case "Distributor":
      return "Reseller";
    default:
      return "Other";
  }
};

// Get country codes to load provinces/states
exports.step = function(input) {
	// Countries
  let res = input.addressBook.items.map(m => m.addressBookAddress.country.id);
  // Unique and quoted
  res = res.filter((v,i,a)=>a.indexOf(v) === i).map(m=>`'${m}'`); 
  if(res && res.length > 0) {
    return {
      q:`SELECT * FROM state WHERE country IN (${res.join(',')})`
    };
  } else {
    return null;
  }
};

// We should use company name for companies
// and alternative name for individuals
exports.step = function(input) {
	return input.customer.isPerson ? 
    [input.customer.firstName, input.customer.middleName, input.customer.lastName]
		.filter(f=>Boolean(f)) // filter out empty fields
		.join(' ')
  	: input.customer.companyName;
};

exports.step = function (input) {
  const billingAddress = input.addressBook.find((f) => f.defaultBilling === true);
  const shippingAddress = input.addressBook.find((f) => f.defaultShipping === true);
  let res = {};
  if(billingAddress){
    if(input.states 
       && billingAddress.addressBookAddress.country
       && billingAddress.addressBookAddress.state){
      const state = input.states.find(f => f.country === billingAddress.addressBookAddress.country.id
                                       && f.shortname === billingAddress.addressBookAddress.state);
      if(state){
        billingAddress.addressBookAddress.state = state.fullname;
      }
    }
    res.billingAddress = billingAddress.addressBookAddress;
  }
  if(shippingAddress){
    if(input.states 
       && shippingAddress.addressBookAddress.country
       && shippingAddress.addressBookAddress.state){
      const state = input.states.find(f => f.country === shippingAddress.addressBookAddress.country.id
                                       && f.shortname === shippingAddress.addressBookAddress.state);
      if(state){
        shippingAddress.addressBookAddress.state = state.fullname;
      }
    }
    res.shippingAddress = shippingAddress.addressBookAddress;
  }
    return res;
};
