exports.step = function(input) {
  // Sort items by startdate
  const sortedItems = input.priceLines.sort((a, b) => new Date(b.startDate).getTime() - new Date(a.startDate).getTime());

  // Keep only the last occurrence of each linenumber
  const linenumberSet = new Set();
  let latestItems = sortedItems.filter(item => {
    if (!linenumberSet.has(item.lineNumber)) {
      linenumberSet.add(item.lineNumber);
      return true;
    }
    return false;
  });

  // if one item is active, it means the subscription is active
  const activeItem = latestItems.find(i => i.status.id === 'ACTIVE');
  if (activeItem) {
    return 'Activated';
  }

  // if no item is active and one item is terminated, it means the subscription is terminated
  const terminatedItem = latestItems.find(i => i.status.id === 'TERMINATED');
  if (terminatedItem) {
    return 'Terminated';
  }

  // if subscription is not active or terminated, still in draft, pending activation without activation or termination order also means that is draft
  return 'Draft';
};
