exports.step = function(input) {
  const fileList = [];
  if (input.signedQuote) {
    fileList.push({ file: input.signedQuote, signedQuote: true });
  }
  if (input.previousQuote) {
    input.previousQuote.name = `Previous-with-AR-Clause-${input.previousQuote.name}`;
    fileList.push({ file: input.previousQuote });
  }
  if (input.purchaseOrder) {
    fileList.push({ file: input.purchaseOrder });
  }
  if (input.unsignedQuote && input.unsignedQuote.success) {
    input.unsignedQuote.file.name = `AR-Quote-${input.unsignedQuote.file.name}`;
    fileList.push({ file: input.unsignedQuote.file });
  }
  //remove duplicates
  const filteredList = fileList.filter((value, index, self) =>
    index === self.findIndex(t => t.file.name === value.file.name)
  );
  filteredList.forEach(i => i.file.name = formatFilename(i.file));
  return filteredList;
};

// Method to get the extension based on the MIME type
function getExtensionFromMimeType(mimeType) {
  switch (mimeType) {
    case 'application/pdf':
      return '.pdf';
    case 'image/jpeg':
      return '.jpg';
    case 'image/png':
      return '.png';
    case 'application/vnd.ms-excel':
      return '.xls';
    default:
      return '';
  }
}

// Method to return the base filename and the extension
function getFilenameParts(file) {
  if (file.name.match(/\.\w{1,4}$/)) {
    const dotIndex = file.name.lastIndexOf(".");
    return {
		baseName: file.name.slice(0, dotIndex),
		extension: file.name.slice(dotIndex)
	};
  }

  return {
	baseName: file.name,
	extension: getExtensionFromMimeType(file.mime_type)
  };
}

// Method to format the filename (add the extension and truncate if necessary)
function formatFilename(file) {
  const maxLength = 199;
  let { baseName, extension } = getFilenameParts(file);

  // Check and truncate the filename if it exceeds the maximum length
  if (baseName.length + extension.length > maxLength) {
    baseName = baseName.slice(0, maxLength - extension.length);
  }

  return `${baseName}${extension}`;
}

// Get instance name (e.g. 4914352-sb1) out of the 'self' link in the REST response
exports.step = function(input) {
	const regex = /^https:\/\/(?<instance>[^.]+)\./;
	const instance = input.self.match(regex).groups.instance;
  return instance;
};

