exports.step = function (input) {
  let account = input.account;
  // Convert state to short name
  const mapStates = input.states.reduce((p,c,i)=>
  {
   p[c.fullname.toLowerCase()]=c.shortname; // FullName to short name mapping
   return p;
  },{});
  const state = account.ShippingState ? account.ShippingState.toLowerCase() : undefined;
  if(mapStates[state]){
    account.ShippingState = mapStates[state]; // We can use short names only
  }
  return account.ShippingState
};

