exports.step = function (input) {
  let account = input.account;
  //Convert country name to id if possible
  const mapCountries = input.countries.reduce((p,c,i)=>
  {
   p[c.name.toLowerCase()]=c.id; // Name to Id mapping
   return p;
  },{});
  mapCountries['usa'] = 'US';
  mapCountries['uk'] = 'GB';
  const country = account.ShippingCountry ? account.ShippingCountry.toLowerCase() : undefined;
  if(mapCountries[country]){
    account.ShippingCountry = mapCountries[country];
  }
  return account.ShippingCountry;
};

exports.step = function (input) {
  let account = input.account;
  account.ShippingCountry = input.country;
  account.ShippingState = input.state;

  let addressBook = {
    items: [],
  };

  let addressBookAddress = {};
  let changed = false;

  if (!account.ShippingCountry 
        || !account.ShippingStreet) {
    return {
      items: [],
      somethingHasChanged: false
    }
  }

  addressBookAddress.addressee = account.Name;
  addressBookAddress.country = { id: account.ShippingCountry };
  addressBookAddress.city = account.ShippingCity;
  addressBookAddress.addr1 = account.ShippingStreet;
  addressBookAddress.state = account.ShippingState;
  addressBookAddress.zip = account.ShippingPostalCode;

  let itemToAdd = {
    addressBookAddress: addressBookAddress,
    defaultShipping: true,
  };
  if (input.address && input.address.defaultBilling === false) {
    // Update an exisitng address
    itemToAdd.id = input.address.id;
    itemToAdd.internalId = input.address.id;
    itemToAdd.addressId = input.address.id.toString();
  }

  if (input.address.label === input.address.addressBookAddress.addr1) {
    itemToAdd.label = addressBookAddress.addr1
  }

  // Detect changes to not make unnecessary update (indefinite update loop prevention)
  changed =
    !input.address ||
    !input.address.addressBookAddress.country ||
    addressBookAddress.country.id !=
      input.address.addressBookAddress.country.id ||
    addressBookAddress.city != input.address.addressBookAddress.city ||
    addressBookAddress.addr1 != input.address.addressBookAddress.addr1 ||
    addressBookAddress.state != input.address.addressBookAddress.state ||
    addressBookAddress.zip != input.address.addressBookAddress.zip;
  addressBook.somethingHasChanged = changed;
  addressBook.items.push(itemToAdd);
  return addressBook;
};
