// Product the BU-related email out of the ISR user email
exports.step = function(input) {
  const name = input.ownerEmail.replace('.invalid','').replace(/@.*/g, '');
  const domain = input.mailboxOverride?input.mailboxOverride:(input.businessUnit.toLowerCase()+'.com');
	return `${name}@${domain}`;
};

//  Changed the script as part of LAMBDA-60930. 
exports.step = function(input, fileInput) {
    if (input.family === 'EngineYard' && input.Type === 'Upsell') {
        return 96;
    } else if (input.Type === 'Renewal' && ['STL'].includes(input.family)) {
        return 163;
    } else if (input.Type === 'Renewal' && ['SupportSoft', 'CoreTrac', 'Smart Routines', 'Pricer', 'Skyvera Misc', 'Ignite Misc'].includes(input.family)) {
        return 94;
    } else if (input.Type === 'Renewal') {
        return 93;
    } else {throw new Error('Outreach sequence ID cannot be determined, family:'+ input.family + ', opp type:' +input.Type+ ', opp id:'+input.Id+'.');} 
};
