// Get the success level code 'SIL', 'GOL, 'PLA'
exports.step = function(input, fileInput) {
	return input.planCode.split("-")[4];
};

function filterIntervals(input, statuses) {
  const subscriptionLines = input.subscriptionLines
    .filter((a) => Number(a.subscriptionLineType.id) === 2 
            && statuses.includes(a.status.id))
    .map((a) => a.lineNumber);

  // extract relevant priceIntervals by line number
  const priceIntervals = input.priceIntervals
  					.filter((a) => subscriptionLines.includes(a.lineNumber));

  return priceIntervals;
}

function getIntervals(input) {
  const priceIntervals = filterIntervals(input, ['ACTIVE', 'CLOSED']);

  // Gets the latest price interval record by startdate for each subscription line record
  let intervals = {};
  priceIntervals.forEach((a) => {
    if (!(String(a.lineNumber) in intervals)) {
      intervals[String(a.lineNumber)] = a;
      return;
    }
    if (new Date(intervals[String(a.lineNumber)].startDate) - new Date(a.startDate) < 0) {
      intervals[String(a.lineNumber)] = a;
    }
  });

  return Object.values(intervals);
}

function getIntervalsEx(input) {
  //no ACTIVE intervals, ARR will be zero, let's search for the 
  //most recent ACTIVE start date (exclude negative amounts)
  const priceIntervals = filterIntervals(input, ['ACTIVE', 'CLOSED', 'TERMINATED']);

  console.log('No ACTIVE among leaf intervals, search for the most recent active..');

  var maxActiveStartDate = new Date(0);
  priceIntervals.forEach(a => {
    console.log(`${a.lineNumber}-${a.status.id}-${a.startDate}`);
    if(a.status.id === 'ACTIVE' && 
       (new Date(a.startDate) - maxActiveStartDate > 0) &&
        a.recurringAmount > 0) {
      maxActiveStartDate = new Date(a.startDate);
    }
  });
  console.log(`Max Date is ${maxActiveStartDate.toISOString()}`);

  //return most recent ACTIVE price intervals
  let intervals = {};
  priceIntervals.forEach((a) => {
    if(a.status.id === 'ACTIVE' &&
       a.recurringAmount > 0 &&
       new Date(a.startDate) >= maxActiveStartDate) {
        intervals[String(a.lineNumber)] = a;
    }
  });  

  return Object.values(intervals);  
}

exports.step = function(input, fileInput) {

  //use default approach to get intervals
  const res = getIntervals(input);

  //check if we got at least a single ACTIVE interval
  if(input.extendedApproach !== 1 || res.filter(x => x.status.id === 'ACTIVE').length > 0) {
    return res;
  }

  //fallback to extended approach
  return getIntervalsEx(input);


};

// Generates query to retrieve the amount of all charges associated with the subscription lines of the subscription
exports.step = function(input, fileInput) {
	return {
    q: `
			SELECT amount
			FROM charge
			WHERE subscriptionline IN (${input.subscriptionLineIds.join(',')})
	`};
};

// Sum all the included interval values
exports.step = function (input, fileInput) {
    const res = input.charges
        .map(m => parseFloat(m.amount))
        .reduce((a, b) => a + b, 0);
    return res ? +res.toFixed(4) : 0;
};

// Annual recurring revenue
exports.step = function(input) {
  if(Number(input.tcv) <= 0.01) { 
    return 0; // Zero TCV contract can't have non-zero ARR
  }
  let totalArr = 0;
  const frequencyCoeff = (f) => {
    switch (f) {
      case 'ANNUALLY': return 1;
      case 'SEMIANNUALLY': return 2;
      case 'QUARTERLY': return 4;
      case 'MONTHLY': return 12;
      case 'SEMIMONTHLY': return 24;
      case 'BIENNIALLY': return .5;
      case 'TRIENNIALLY': return 1.0 / 3.0;

      default: return 0;
    }
  };

  for (let i = 0; i < input.lines.length; i++) {
    if(input.lines[i].status.id !== 'ACTIVE'
       || !(input.lines[i].recurringAmount)) {
      continue;
    }
    const lineArr = input.lines[i].recurringAmount * frequencyCoeff(input.lines[i].frequency.id) / input.lines[i].repeatEvery;
    totalArr += lineArr;
  }

  return +totalArr.toFixed(4);
};

// Extracts the subscription line id from the subscription line records into an array
exports.step = function(input, fileInput) {
	return input.subscriptionLines.map((a) => a.subscriptionLine);
};
