exports.step = function (input) {
  return {
    q: `
SELECT
    s.id AS id,
    s.billingsubscriptionstatus AS substatus,
    CASE
        WHEN hist.subscription IS NULL THEN s.custrecord_parent_subscription
        ELSE hist.subscription
    END AS parentsubscription
FROM    
    subscription s
    LEFT JOIN subscriptionRenewalHistory hist ON hist.newsubscription = s.id    
WHERE
    CASE
        WHEN hist.subscription IS NULL THEN s.custrecord_parent_subscription
        ELSE hist.subscription
    END = ${input.row.parentsubscription}
AND s.id != ${input.row.subscription}
AND s.billingsubscriptionstatus IN ('DRAFT', 'PENDING_ACTIVATION', 'ACTIVE')
AND (s.custrecord_isprime is null OR s.custrecord_isprime <> 1)
`
  };
};

exports.step = function(input) {
  const activeItems = input.subscription.subscriptionLine.items.find(i => {
    if (!['ACTIVE','PENDING_ACTIVATION'].includes(i.status.id)) {
      return false;
    }
    const lastPriceInterval = input.subscription.priceInterval.items.filter(p => p.lineNumber === i.lineNumber).pop();
    return lastPriceInterval.status.id === 'ACTIVE'
  });
  return {
    hasActiveItems: !!activeItems
  }
};

