// Get instance name (e.g. 4914352-sb1) out of the 'self' link in the REST response
exports.step = function(input) {
	const regex = /^https:\/\/(?<instance>[^.]+)\./;
	const instance = input.self.match(regex).groups.instance;
  return instance;
};

exports.step = function (input) {
  const customerName = input.customer.isPerson
    ? [
        input.customer.firstName,
        input.customer.middleName,
        input.customer.lastName,
      ]
        .filter((f) => Boolean(f)) // filter out empty fields
        .join(" ")
    : input.customer.companyName;

  var isrTag = `${input.isrEmail}`;
	if(input.isrJiraAccountId) {
    isrTag = `[~accountid:${input.isrJiraAccountId}], email: ${input.isrEmail}`;
  }

  let subject = `Invoice Request - ${customerName} -${input.subscriptionName}`;
  const descriptionHeader = `Ticket generated by Trilogy Sales Infrastructure.\n\n*Sales Rep*: ${isrTag}`;
  let descriptionFooter = input.opportunityType === 'renewal' ? 'Sales Team: Core Renewals\n' : '';
  descriptionFooter += `Activate the subscription and generate the invoice.`;
  const subscriptionLink = `[${input.subscriptionId}|https://4914352.app.netsuite.com/app/accounting/subscription/subscription.nl?id=${input.subscriptionId}]`;
  const quoteLink = `[${input.nsQuoteId}|https://4914352.app.netsuite.com/app/accounting/transactions/estimate.nl?id=${input.nsQuoteId}]`;
  const manualo2cText = `Please replace the renewal subscription ${subscriptionLink} with a new Renewal Subscription as per the Quote ${quoteLink}.`;
  const additionalInstructions = input.footerText?`*Important Note*: ${input.footerText}`:'';
  let description;
  let descriptionText;
  switch(input.winType) {
    case 'Auto-Renew':
      subject = `AR - ${subject}`;
      descriptionText = input.manualo2c ? `
This is an Auto-Renewal Invoice request.
${manualo2cText}` : `
This is an Auto-Renewal Invoice request for renewal subscription ${subscriptionLink}.`;
      description = `AUTO-RENEWAL
${descriptionHeader}
${descriptionText}
Quote: AR.
Previously Signed Quote with AR Clause attached to this ticket.
${descriptionFooter}
${additionalInstructions}`;
      break;
    case 'PO Received':
      description = `${descriptionHeader}
${input.opportunityType} Opportunity won for draft subscription ${subscriptionLink}.
PO Only. PO Attached to this ticket.
${descriptionFooter}
${additionalInstructions}`;
      break;
    case 'Quote Signed':
      descriptionText = input.manualo2c ? `
${manualo2cText}` : `
${input.opportunityType} Opportunity won for draft subscription ${subscriptionLink}.
Signed quote ${quoteLink}.
${additionalInstructions}`;
      description = `${descriptionHeader}${descriptionText}
${descriptionFooter}`;
      break;
    default:
      throw new Error(`Unexpected win type: ${input.winType}`)
  }
  return {
    subject: subject,
    description: description
  };
};
