// if the pdf file doesn't end with .pdf, From base64 doesn't return mime type and upload to SF doesn't work
exports.step = function(input) {
	if (input.fileType === '_PDF' && input.name.substr(input.name.length -4).toLowerCase() !== '.pdf') {
    return `${input.name}.pdf`;
  } else if (input.fileType === '_PNGIMAGE' && input.name.substr(input.name.length -4).toLowerCase() !== '.png') {
    return `${input.name}.png`;
  } else if (input.fileType === '_EXCEL' && input.name.substr(input.name.length -4).toLowerCase() !== '.xls') {
    return `${input.name}.xls`;
  } else if (input.fileType === '_JPGIMAGE' && input.name.substr(input.name.length -4).toLowerCase() !== '.jpg') {
    return `${input.name}.jpg`;
  }
  return input.name;
};

exports.step = function(input) {
  const regex = /id=(?<id>\d+)/;
  return input.url.match(regex)?.groups.id;
};

//Gets Quote Id from url on name
//If name doesn't match regex, it means that the file is attached on the contractual documents and not on the quote
exports.step = function(input) {
	const regex = /^https:\/\/(?<instance>[^.]+).app.netsuite.com\/app\/accounting\/transactions\/estimate.nl\?id=(?<id>[\d+]+)$/;
	const id = input.name.match(regex)?.groups.id;
  return id
};

exports.step = function (input) {
  const request = {
    url: `https://${input.instance}.restlets.api.netsuite.com/app/site/hosting/restlet.nl?script=customscript_si_main_main_restlet&deploy=customdeploy_si_main_main`,
    payload: {
      op: "search.internalAttachedFields",
      recordType: input.recordType,
      id: input.id,
    }
  };
  return request;
};

exports.step = function(input) {
  return !!input.salesforceFiles.find(i => i.ContentDocument.Title === input.fileName
                                      // check filename without extension
                                      || i.ContentDocument.Title === input.fileName.replace(/\.\w{1,4}$/, "")
                                     );
};

// Get instance name (e.g. 4914352-sb1) out of the 'self' link in the REST response
exports.step = function(input) {
	const regex = /^https:\/\/(?<instance>[^.]+)\./;
	const instance = input.self.match(regex).groups.instance;
  return instance;
};
