exports.step = function(input) {
	let customer = input.end_user;
  const distributor = input.partners.find(i => i.Role === 'Distributor');
  const distributorId = distributor ? distributor.AccountTo.NetSuite_ID__c : null;

  let reseller = input.partners.find(i => i.Role === 'VAR/Reseller');
  if (!reseller) {
    reseller = input.partners.find(i => !i.Role);
  }
  const resellerId = reseller ? reseller.AccountTo.NetSuite_ID__c : null;

  let sfPartnerId = null;
  if (distributorId) {
    customer = distributorId;
    sfPartnerId = distributor.AccountTo.attributes.url.split('/').pop();
  } else if(resellerId) {
    customer = resellerId;
    sfPartnerId = reseller.AccountTo.attributes.url.split('/').pop();
  } else {
  	customer = input.end_user
  }
  return {
    customer: customer,
    distributor: distributorId,
    reseller: resellerId,
    endUser: input.end_user,
    sfPartnerId: sfPartnerId
  };
};

exports.step = function(input) {
  var customerName = input.customer.isPerson ? 
    [input.customer.firstName, input.customer.middleName, input.customer.lastName]
		.filter(f=>Boolean(f)) // filter out empty fields
		.join(' ')
  	: input.customer.companyName;
  // Filter out special characters
  customerName = customerName.replace(/[\n\r]/g, ' ');
  const resellerAgreement = input.resellerAgreementLink ?
    `Here is the Reseller Agreement: ${input.resellerAgreementLink}
` :
    '';

  var isrTag = `${input.isrEmail}`;
	if(input.isrJiraAccountId) {
    isrTag = `[~accountid:${input.isrJiraAccountId}], email: ${input.isrEmail}`;
  }
  const hasPartner = !!input.partnerId;
  const qcerAction3 = hasPartner ? `QCer Action 3 - EUA: If a non-expired & signed EUA  does not exist on the end user, then QC and send the Generated EUA using the playbook: [SENDING EUA FROM NETSUITE|https://docs.google.com/document/d/119pYniQ4Ttp_LTKhcbyWg4lDLczFLjvJpGqy5uI-nFg/edit]
` : '';
  const additionalInstructions = input.footerText?`*Important Note*: ${input.footerText}\n`:'';
  let qcerActionExtra1 = '';
  let masterContractInstructions = '';
  if (input.nsQuoteType === 'FullTCs') {
    qcerActionExtra1 = 'If this quote should be without Full T&Cs, please, follow the instructions to set Master Contract at the end of the description of this ticket.';
    masterContractInstructions = `
Instructions to set Master Contract:
1. Go to the Contractual documents for the subscription which already has Full T&Cs.
2. Edit this Contractual documents:
   a. LINK TO CONTRACT PDF:  NetSuite link to the Signed Quote PDF.
   b. REVENUE TYPE: Set it to SaaS, On-Premise, or Professional Services (Can be taken from the main item on the Signed Quote) or set it to Support if the Quote is of type Maintenance (only support item exists in Quote).
   c. QUOTE: Set it to the signed quote in the format Quote #<Quote ID>.
   d. IS MASTER CONTRACT: Set to true.
   e. ESW PAPER: Set to true if the signed quote is ESW standard quote
   f. VERSION: Set it to the signed T&Cs version found at the footer of signed Full T&Cs if its ESW Paper.`;
  } else if (input.nsQuoteType === 'NoTCs') {
    qcerActionExtra1 = 'If this quote should be with Full T&Cs, please, follow the instructions to unset Master Contract at the end of the description of this ticket.';
    masterContractInstructions = `
Instructions to unset Master Contract:
1. Find in the chain of parent subscriptions to the Contractual documents that has Is Master Contract set to true.
2. Edit this Contractual documents and set the IS MASTER CONTRACT to false.
3. Save this Contractual documents.`;
  }
	return {
  	subject: `Quote Request - ${customerName}`, // [quote] prefix will be added by Jira
    description: `The Quote with ID [${input.nsQuoteId}|https://4914352.app.netsuite.com/app/accounting/transactions/estimate.nl?id=${input.nsQuoteId}] was generated by Sales Integration.
${additionalInstructions}
${resellerAgreement}

*Sales Rep*: ${isrTag}

Please perform the following steps.
1.  QC the Quote as normal Quote unit (No DS file required. The generated quote takes place of the DS).
2.  Assume the Data which was earlier validated through DS is always correct in this case. 
3.  No Action by Input or RFW worker required.
4.  You might observe a new subscription plan added to the quote as an item which is a new format and should not be failed for this.
5.  Open NetSuite quote by clicking the Quote link in the ticket description.
6.  Click the Agreements tab under NetSuite Quote and open the linked agreement.
7.  Verify if the NetSuite Agreement has correct recipients in the correct order under Recipients tab.
8.  Verify if the correct Terms and Conditions files are attached to the NetSuite Agreement under Documents tab as per the QC rules. 
9.  Verify if the Billing and Shipping addresses on the Quote are correct as per the NetSuite Customer's Billing/Shipping addresses. 
10. Verify that the checkbox [PREVIEW DOCUMENT OR POSITION SIGNATURES] is pre-checked.
11. QCer please run QC checks on the quote PDF.
12. Click the [Send For Signature] button in NetSuite agreement. It will open the PDF preview of the quote QC. 

QCer Action 1- Send out the Quote once all QC checks are met. ${qcerActionExtra1}
QCer Action 2 - Mention the O2C case reference in the Memo field on the Quote once sent out.
${qcerAction3}
Note: 
If you need to close the quote preview window in NetSuite without sending the quote for signature, please give it at least two minutes to fully load and then close the preview window if needed.
${masterContractInstructions}`
	};
}
