exports.step = function(input) {
  // Checking first for closed lines because we can have lines that were terminated before 
  // the end of the subscription, but if we have one line that is closed, 
  // it means at least one item was active until the end date of the subscription
  const closedLine = input.lines.find(i => i.status.id === 'CLOSED');
  if (closedLine) {
    return 'CLOSED';
  }
  const terminatedLine = input.lines.find(i => i.status.id === 'TERMINATED');
  if (terminatedLine) {
    return 'TERMINATED';
  }
	return null;
};

