exports.step = function(input) {
  // Sort items by startdate
  const sortedItems = input.priceLines.sort((a, b) => new Date(b.startdate).getTime() - new Date(a.startdate).getTime());

  // Keep only the last occurrence of each linenumber
  const linenumberSet = new Set();
  let latestItems = sortedItems.filter(item => {
    if (!linenumberSet.has(item.lineNumber)) {
      linenumberSet.add(item.lineNumber);
      return true;
    }
    return false;
  });

  // filter out not included lines
  latestItems = latestItems.filter(i => i.status.id !== 'NOT_INCLUDED');
  // if opportunity filter out lines that are not draft or pending activation
  if (input.status !== 'DRAFT') {
    latestItems = latestItems.filter(i => i.status.id !== 'DRAFT' && i.status.id !== 'PENDING_ACTIVATION');
  }
  console.log(latestItems)
  return latestItems.reduce((total, i) => {
    return total + getARR(i.recurringAmount, i.frequency.id, i.repeatEvery)
  }, 0);
};

function getARR(recurringAmount, frequency, repeatEvery) {
  let frequencyMultiplier;
  switch (frequency.toUpperCase()) {
    case 'MONTHLY':
      frequencyMultiplier = 12;
      break;
    case 'WEEKLY':
      frequencyMultiplier = 52;
      break;
    default: //ANNUALLY OR ANY OTHER NOT HANDLED
      frequencyMultiplier = 1;
  }

  return recurringAmount * frequencyMultiplier / repeatEvery;
}

exports.step = function(input) {
  const terminatedLines = input.lines.filter(i => i.status.id === 'TERMINATED');
  terminatedLines.sort((a,b) => new Date(b.terminationDate) - new Date(a.terminationDate));
  return terminatedLines[0].terminationDate;
};
