// Years and months are supported only
exports.step = function(input, fileInput) {
	return input.termUnit === 'MONTHS' ? input.termDuration : input.termDuration*12;
};

// Construct the product codes from the plan code
exports.step = function(input, fileInput) {
  const fields = input.plan.split('-');
  return {
      mainProductCode: `${fields[0]}-${fields[1]}-${fields[2]}-${fields[3]}`,
      supportProductCode: `${fields[0]}-${fields[1]}-${fields[2]}-${fields[4]}`
  }
};

// Since Currency is available in Beta only,
// we use suiteQL to get the code
exports.step = function (input) {
  return {
    q:
      `SELECT cur.symbol AS currencyisocode,
FROM subscription s
JOIN currency cur on s.currency = cur.id
WHERE s.id = ${input.subscriptionId}`
  };
};
